import styled from "styled-components";

export const Navbar = styled.div`
	padding: 20px;
	display: grid;
	grid-gap: 7px;
	grid-template-columns: 120px 120px 120px auto 120px 120px;
	align-items: center;
	justify-items: center;
	position: static;
	@media (max-width: 700px) {
		display: block;
	}
`;

export const Menu = styled.i`
	display: none;
	color: white;
	@media (max-width: 700px) {
		display:block
		position: absolute;
		right: 0;
		top: 0;
	}
`;

export const Brand = styled.h1`
	font-size: 3rem;
	color: white;
	margin: 0px;
	font-family: "Quicksand", sans-serif;
`;
