import React from "react";
import styled from "styled-components";

function Jumbotron() {
	return (
		<Section>
			<Title>
				You love your pets, <br /> so do we.
			</Title>
			<Paragraph>
				At Sit.com we provide the most quality <br />
				caretakers to your beloved pets
			</Paragraph>
			<Button>Find a Sitter!</Button>
			<Footer>
				Are you looking for a pet sitting job? <Link>Apply Now.</Link>
			</Footer>
		</Section>
	);
}

let Section = styled.section`
	width: 60%;
	margin: 80px auto 0px auto;
`;

let Title = styled.h1`
	font-size: 3rem;
	color: white;
	font-family: "Quicksand", sans-serif;
`;

let Paragraph = styled.p`
	font-size: 1.4rem;
	color: white;
`;

let Button = styled.button`
	background: none;
	color: white;
	width: 170px;
	height: 50px;
	font-size: 1.5rem;
	outline: none;
	cursor: pointer;
	border: 1px solid white;
	&:hover {
		color: black; //
		background: white;
	}
`;

let Footer = styled.p`
	color: white;
`;

let Link = styled.a`
	font-size: 1rem;
`;

export default Jumbotron;
